import React from 'react';
import { Task } from '../types/task.types';
import TaskItem from './TaskItem';
import styles from './TaskListUser.module.css';

interface TaskListUserProps {
  tasks: Task[];
  loading: boolean;
  onEdit: (task: Task) => void;
  onDelete: (taskId: string) => void;
  onCreateTask: () => void;
}

const TaskListUser: React.FC<TaskListUserProps> = ({
  tasks,
  loading,
  onEdit,
  onDelete,
  onCreateTask
}) => {
  if (loading) {
    return (
      <div className={styles.loadingState}>
        Chargement de vos t√¢ches...
      </div>
    );
  }

  if (tasks.length === 0) {
    return (
      <div className={styles.emptyState}>
        <div className={styles.emptyIcon}>üìã</div>
        <h3 className={styles.emptyTitle}>Aucune t√¢che trouv√©e</h3>
        <p className={styles.emptyText}>
          Vous n'avez pas encore de t√¢ches. Commencez par en cr√©er une !
        </p>
        <button onClick={onCreateTask} className={styles.emptyButton}>
          Cr√©er ma premi√®re t√¢che
        </button>
      </div>
    );
  }

  // Grouper les t√¢ches par statut
  const tasksByStatus = {
    pending: tasks.filter(t => t.status === 'pending'),
    inProgress: tasks.filter(t => t.status === 'in-progress'),
    completed: tasks.filter(t => t.status === 'completed')
  };

  return (
    <div className={styles.taskListContainer}>
      <div className={styles.statsBar}>
        <div className={styles.statItem}>
          <span className={styles.statLabel}>Total</span>
          <span className={styles.statValue}>{tasks.length}</span>
        </div>
        <div className={styles.statItem}>
          <span className={styles.statLabel}>En attente</span>
          <span className={`${styles.statValue} ${styles.pending}`}>
            {tasksByStatus.pending.length}
          </span>
        </div>
        <div className={styles.statItem}>
          <span className={styles.statLabel}>En cours</span>
          <span className={`${styles.statValue} ${styles.inProgress}`}>
            {tasksByStatus.inProgress.length}
          </span>
        </div>
        <div className={styles.statItem}>
          <span className={styles.statLabel}>Termin√©</span>
          <span className={`${styles.statValue} ${styles.completed}`}>
            {tasksByStatus.completed.length}
          </span>
        </div>
      </div>

      <div className={styles.tasksList}>
        {tasks.map(task => (
          <TaskItem
            key={task.id}
            task={task}
            onEdit={onEdit}
            onDelete={onDelete}
            isAdmin={false}
          />
        ))}
      </div>
    </div>
  );
};

export default TaskListUser;
