import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { Task, CreateTaskDto, UpdateTaskDto, TaskStatus } from '../types/task.types';
import apiService from '../services/api';
import Header from '../components/Header';
import TaskItem from '../components/TaskItem';
import TaskForm from '../components/TaskForm';
import FormModal from '../components/FormModal';
import Modal from '../components/Modal';
import Spinner from '../components/Spinner';
import styles from './MyTasks.module.css';

const MyTasks: React.FC = () => {
  const { user } = useAuth();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [isFormModalOpen, setIsFormModalOpen] = useState(false);
  const [selectedTask, setSelectedTask] = useState<Task | undefined>(undefined);

  const [filters, setFilters] = useState({
    status: '',
    search: ''
  });

  const [modal, setModal] = useState({
    isOpen: false,
    title: '',
    message: '',
    type: 'info' as 'success' | 'error' | 'warning' | 'info'
  });

  // Calculer les statistiques
  const stats = {
    total: tasks.length,
    pending: tasks.filter(t => t.status === 'pending').length,
    inProgress: tasks.filter(t => t.status === 'in-progress').length,
    completed: tasks.filter(t => t.status === 'completed').length
  };

  // Charger les tâches de l'utilisateur
  const loadMyTasks = async () => {
    try {
      setLoading(true);
      const response = await apiService.getMyTasks(
        1,
        100,
        filters.status || undefined,
        filters.search || undefined
      );
      setTasks(response.data);
    } catch (error: any) {
      setModal({
        isOpen: true,
        title: 'Erreur',
        message: 'Impossible de charger vos tâches',
        type: 'error'
      });
    } finally {
      setLoading(false);
    }
  };
useEffect(() => {
  const timer = setTimeout(() => {
    loadMyTasks();
  }, 100);

  return () => clearTimeout(timer);
}, [filters]);

  // Ouvrir le formulaire pour créer une tâche
  const handleCreateTask = () => {
    setSelectedTask(undefined);
    setIsFormModalOpen(true);
  };

  // Ouvrir le formulaire pour modifier une tâche
  const handleEditTask = (task: Task) => {
    setSelectedTask(task);
    setIsFormModalOpen(true);
  };

  // Soumettre le formulaire (création ou modification)
  const handleSubmitTask = async (data: CreateTaskDto) => {
    try {
      if (selectedTask) {
        await apiService.updateTask(selectedTask.id, data as UpdateTaskDto);
        setModal({
          isOpen: true,
          title: 'Succès',
          message: 'Tâche modifiée avec succès',
          type: 'success'
        });
      } else {
        await apiService.createTask(data);
        setModal({
          isOpen: true,
          title: 'Succès',
          message: 'Tâche créée avec succès',
          type: 'success'
        });
      }
      setIsFormModalOpen(false);
      loadMyTasks();
    } catch (error: any) {
      setModal({
        isOpen: true,
        title: 'Erreur',
        message: error.response?.data?.message || 'Une erreur est survenue',
        type: 'error'
      });
    }
  };

  // Supprimer une tâche
  const handleDeleteTask = async (taskId: string) => {
    if (!window.confirm('Êtes-vous sûr de vouloir supprimer cette tâche ?')) {
      return;
    }

    try {
      await apiService.deleteTask(taskId);
      setModal({
        isOpen: true,
        title: 'Succès',
        message: 'Tâche supprimée avec succès',
        type: 'success'
      });
      loadMyTasks();
    } catch (error: any) {
      setModal({
        isOpen: true,
        title: 'Erreur',
        message: 'Impossible de supprimer la tâche',
        type: 'error'
      });
    }
  };

  const handleFilterChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleStatusFilter = (status: string) => {
    setFilters(prev => ({
      ...prev,
      status: prev.status === status ? '' : status
    }));
  };

  const closeModal = () => {
    setModal(prev => ({ ...prev, isOpen: false }));
  };

 
  return (
    <div className={styles.pageContainer}>
      <Header />

      <main className={styles.mainContent}>
        <div className={styles.container}>
          {/* Hero Section */}
          <div className={styles.heroSection}>
            <div className={styles.heroContent}>
              <h1 className={styles.heroTitle}>Mes Tâches</h1>
              <p className={styles.heroSubtitle}>
                Bonjour {user?.name} ! Gérez vos tâches efficacement
              </p>
            </div>
            <button onClick={handleCreateTask} className={styles.createButton}>
              <svg className={styles.createIcon} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <line x1="12" y1="5" x2="12" y2="19"></line>
                <line x1="5" y1="12" x2="19" y2="12"></line>
              </svg>
              Nouvelle Tâche
            </button>
          </div>

          {/* Cards de statistiques */}
<div className={styles.statsGrid}>
  <div className={`${styles.statCard} ${styles.statCardTotal}`}>
    <div className={styles.statIcon}>
      <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
        <path d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
      </svg>
    </div>
    <div className={styles.statContent}>
      <div className={styles.statValue}>{stats.total}</div>
      <div className={styles.statLabel}>Total</div>
    </div>
  </div>

  <div className={`${styles.statCard} ${styles.statCardPending}`}>
    <div className={styles.statIcon}>
      <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
        <circle cx="12" cy="12" r="10"></circle>
        <polyline points="12 6 12 12 16 14"></polyline>
      </svg>
    </div>
    <div className={styles.statContent}>
      <div className={styles.statValue}>{stats.pending}</div>
      <div className={styles.statLabel}>En attente</div>
    </div>
  </div>

  <div className={`${styles.statCard} ${styles.statCardInProgress}`}>
    <div className={styles.statIcon}>
      <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
        <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z" />
      </svg>
    </div>
    <div className={styles.statContent}>
      <div className={styles.statValue}>{stats.inProgress}</div>
      <div className={styles.statLabel}>En cours</div>
    </div>
  </div>

  <div className={`${styles.statCard} ${styles.statCardCompleted}`}>
    <div className={styles.statIcon}>
      <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
        <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
        <polyline points="22 4 12 14.01 9 11.01"></polyline>
      </svg>
    </div>
    <div className={styles.statContent}>
      <div className={styles.statValue}>{stats.completed}</div>
      <div className={styles.statLabel}>Terminées</div>
    </div>
  </div>
</div>

          {/* Section Filtres */}
          <div className={styles.filtersSection}>
            <div className={styles.searchBar}>
              <svg className={styles.searchIcon} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <circle cx="11" cy="11" r="8"></circle>
                <path d="m21 21-4.35-4.35"></path>
              </svg>
              <input
                type="text"
                name="search"
                value={filters.search}
                onChange={handleFilterChange}
                placeholder="Rechercher une tâche..."
                className={styles.searchInput}
              />
            </div>

            <div className={styles.filterChips}>
              <button
                onClick={() => handleStatusFilter('')}
                className={`${styles.filterChip} ${filters.status === '' ? styles.filterChipActive : ''}`}
              >
                Tous
              </button>
              <button
                onClick={() => handleStatusFilter('pending')}
                className={`${styles.filterChip} ${styles.filterChipPending} ${filters.status === 'pending' ? styles.filterChipActive : ''}`}
              >
                En attente
              </button>
              <button
                onClick={() => handleStatusFilter('in-progress')}
                className={`${styles.filterChip} ${styles.filterChipInProgress} ${filters.status === 'in-progress' ? styles.filterChipActive : ''}`}
              >
                En cours
              </button>
              <button
                onClick={() => handleStatusFilter('completed')}
                className={`${styles.filterChip} ${styles.filterChipCompleted} ${filters.status === 'completed' ? styles.filterChipActive : ''}`}
              >
                Terminées
              </button>
            </div>
          </div>

          {/* Liste des tâches */}
          {!tasks || tasks.length === 0 ? (
            <div className={styles.emptyState}>
              <div className={styles.emptyIcon}>
                <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <path d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2" />
                </svg>
              </div>
              <h3 className={styles.emptyTitle}>Aucune tâche trouvée</h3>
              <p className={styles.emptyText}>
                {filters.search || filters.status
                  ? 'Essayez de modifier vos filtres'
                  : 'Commencez par créer votre première tâche'}
              </p>
              {!filters.search && !filters.status && (
                <button onClick={handleCreateTask} className={styles.emptyButton}>
                  Créer ma première tâche
                </button>
              )}
            </div>
          ) : (
            <div className={styles.tasksList}>
              {tasks.map(task => (
                <TaskItem
                  key={task.id}
                  task={task}
                  onEdit={handleEditTask}
                  onDelete={handleDeleteTask}
                  isAdmin={false}
                />
              ))}
            </div>
          )}
        </div>
      </main>

      <FormModal
        isOpen={isFormModalOpen}
        onClose={() => setIsFormModalOpen(false)}
      >
        <TaskForm
          task={selectedTask}
          isAdmin={false}
          onSubmit={handleSubmitTask}
          onCancel={() => setIsFormModalOpen(false)}
        />
      </FormModal>

      <Modal
        isOpen={modal.isOpen}
        onClose={closeModal}
        title={modal.title}
        message={modal.message}
        type={modal.type}
      />
    </div>
  );
};

export default MyTasks;
