import React from 'react';
import { Task } from '../types/task.types';
import styles from './TaskItem.module.css';

interface TaskItemProps {
  task: Task;
  onEdit: (task: Task) => void;
  onDelete: (taskId: string) => void;
  isAdmin?: boolean;
}

const TaskItem: React.FC<TaskItemProps> = ({ task, onEdit, onDelete, isAdmin = false }) => {
  // Fonction pour formater la date
  const formatDate = (dateString: string): string => {
    const date = new Date(dateString);
    return date.toLocaleDateString('fr-FR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  // Fonction pour obtenir la classe CSS selon le statut
  const getStatusClass = (status: string): string => {
    switch (status) {
      case 'completed':
        return styles.statusCompleted;
      case 'in-progress':
        return styles.statusInProgress;
      case 'pending':
        return styles.statusPending;
      default:
        return styles.statusPending;
    }
  };

  // Fonction pour obtenir le texte du statut en français
  const getStatusText = (status: string): string => {
    switch (status) {
      case 'completed':
        return 'Terminé';
      case 'in-progress':
        return 'En cours';
      case 'pending':
        return 'En attente';
      default:
        return status;
    }
  };

  return (
    <div className={styles.taskCard}>
      <div className={styles.taskHeader}>
        <h3 className={styles.taskTitle}>{task.title}</h3>
        <span className={`${styles.statusBadge} ${getStatusClass(task.status)}`}>
          {getStatusText(task.status)}
        </span>
      </div>

      {task.description && (
        <p className={styles.taskDescription}>{task.description}</p>
      )}

      <div className={styles.taskInfo}>
        {task.dueDate && (
          <div className={styles.infoItem}>
            <span className={styles.infoLabel}>Date limite:</span>
            <span className={styles.infoValue}>{formatDate(task.dueDate)}</span>
          </div>
        )}

        {isAdmin && task.user && (
          <div className={styles.infoItem}>
            <span className={styles.infoLabel}>Créé par:</span>
            <span className={styles.infoValue}>{task.user.name}</span>
          </div>
        )}

        {task.assignedTo && (
          <div className={styles.assignedBadge}>
            Assigné à: {task.assignedTo.name}
          </div>
        )}
      </div>

      <div className={styles.taskActions}>
        <button 
          onClick={() => onEdit(task)} 
          className={styles.editButton}
        >
          Modifier
        </button>
        <button 
          onClick={() => onDelete(task.id)} 
          className={styles.deleteButton}
        >
          Supprimer
        </button>
      </div>
    </div>
  );
};

export default TaskItem;
