import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import styles from './Pagination.module.css';

interface PaginationProps {
  currentPage: number;
  totalPages: number;
  onPageChange: (page: number) => void;
}

const Pagination: React.FC<PaginationProps> = ({
  currentPage,
  totalPages,
  onPageChange
}) => {
  const [portalRoot, setPortalRoot] = useState<HTMLElement | null>(null);

  useEffect(() => {
    // Créer ou récupérer le div portal
    let portalDiv = document.getElementById('pagination-portal');
    
    if (!portalDiv) {
      portalDiv = document.createElement('div');
      portalDiv.id = 'pagination-portal';
      document.body.appendChild(portalDiv);
    }

    setPortalRoot(portalDiv);

    // Cleanup : supprimer le portal quand le composant est démonté
    return () => {
      if (portalDiv && document.body.contains(portalDiv)) {
        document.body.removeChild(portalDiv);
      }
    };
  }, []);

  if (!portalRoot) {
    return null;
  }

  return ReactDOM.createPortal(
    <div className={styles.paginationContainer}>
      <button
        onClick={() => onPageChange(currentPage - 1)}
        disabled={currentPage === 1}
        className={styles.paginationButton}
        aria-label="Page précédente"
      >
        ←
      </button>

      <span className={styles.paginationInfo}>
        Page {currentPage} / {totalPages}
      </span>

      <button
        onClick={() => onPageChange(currentPage + 1)}
        disabled={currentPage === totalPages}
        className={styles.paginationButton}
        aria-label="Page suivante"
      >
        →
      </button>
    </div>,
    portalRoot
  );
};

export default Pagination;
