import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { Task, CreateTaskDto, UpdateTaskDto } from '../types/task.types';
import { User } from '../types/auth.types';
import apiService from '../services/api';
import authService from '../services/authService';
import Header from '../components/Header';
import TaskItem from '../components/TaskItem';
import TaskForm from '../components/TaskForm';
import FormModal from '../components/FormModal';
import Modal from '../components/Modal';
import styles from './Dashboard.module.css';

const Dashboard: React.FC = () => {
  const { user } = useAuth();
  const [tasks, setTasks] = useState<Task[]>([]);
  const [users, setUsers] = useState<User[]>([]);
  const [loading, setLoading] = useState(true);
  const [isFormModalOpen, setIsFormModalOpen] = useState(false);
  const [selectedTask, setSelectedTask] = useState<Task | undefined>(undefined);

  const [filters, setFilters] = useState({
    status: '',
    search: ''
  });

  const [modal, setModal] = useState({
    isOpen: false,
    title: '',
    message: '',
    type: 'info' as 'success' | 'error' | 'warning' | 'info'
  });

  // Charger toutes les t√¢ches et tous les utilisateurs
  const loadData = async () => {
    try {
      setLoading(true);
      const [tasksResponse, usersResponse] = await Promise.all([
        apiService.getAllTasks(
          1,
          100,
          filters.status || undefined,
          filters.search || undefined
        ),
        authService.getUsers()
      ]);
      setTasks(tasksResponse.data);
      setUsers(usersResponse);
    } catch (error: any) {
      setModal({
        isOpen: true,
        title: 'Erreur',
        message: 'Impossible de charger les donn√©es',
        type: 'error'
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadData();
  }, [filters]);

  // Ouvrir le formulaire pour cr√©er une t√¢che
  const handleCreateTask = () => {
    setSelectedTask(undefined);
    setIsFormModalOpen(true);
  };

  // Ouvrir le formulaire pour modifier une t√¢che
  const handleEditTask = (task: Task) => {
    setSelectedTask(task);
    setIsFormModalOpen(true);
  };

  // Soumettre le formulaire (cr√©ation ou modification)
  const handleSubmitTask = async (data: CreateTaskDto) => {
    try {
      if (selectedTask) {
        await apiService.updateTask(selectedTask.id, data as UpdateTaskDto);
        setModal({
          isOpen: true,
          title: 'Succ√®s',
          message: 'T√¢che modifi√©e avec succ√®s',
          type: 'success'
        });
      } else {
        await apiService.createTask(data);
        setModal({
          isOpen: true,
          title: 'Succ√®s',
          message: 'T√¢che cr√©√©e avec succ√®s',
          type: 'success'
        });
      }
      setIsFormModalOpen(false);
      loadData();
    } catch (error: any) {
      setModal({
        isOpen: true,
        title: 'Erreur',
        message: error.response?.data?.message || 'Une erreur est survenue',
        type: 'error'
      });
    }
  };

  // Supprimer une t√¢che
  const handleDeleteTask = async (taskId: string) => {
    if (!window.confirm('√ätes-vous s√ªr de vouloir supprimer cette t√¢che ?')) {
      return;
    }

    try {
      await apiService.deleteTask(taskId);
      setModal({
        isOpen: true,
        title: 'Succ√®s',
        message: 'T√¢che supprim√©e avec succ√®s',
        type: 'success'
      });
      loadData();
    } catch (error: any) {
      setModal({
        isOpen: true,
        title: 'Erreur',
        message: 'Impossible de supprimer la t√¢che',
        type: 'error'
      });
    }
  };

  const handleFilterChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const closeModal = () => {
    setModal(prev => ({ ...prev, isOpen: false }));
  };

  // Calculer les statistiques
  const stats = {
    total: tasks?.length || 0,
    pending: tasks?.filter(t => t.status === 'pending').length || 0,
    inProgress: tasks?.filter(t => t.status === 'in-progress').length || 0,
    completed: tasks?.filter(t => t.status === 'completed').length || 0,
    assigned: tasks?.filter(t => t.assignedToId).length || 0
  };

  return (
    <div className={styles.pageContainer}>
      <Header />

      <main className={styles.mainContent}>
        <div className={styles.container}>
          <div className={styles.pageHeader}>
            <div>
              <h1 className={styles.pageTitle}>Dashboard Admin</h1>
              <p className={styles.pageSubtitle}>
                Bienvenue {user?.name}, g√©rez toutes les t√¢ches
              </p>
            </div>
            <button onClick={handleCreateTask} className={styles.createButton}>
              Nouvelle T√¢che
            </button>
          </div>

          <div className={styles.statsGrid}>
            <div className={styles.statCard}>
              <div className={styles.statIcon}>üìä</div>
              <div className={styles.statContent}>
                <span className={styles.statLabel}>Total</span>
                <span className={styles.statValue}>{stats.total}</span>
              </div>
            </div>

            <div className={styles.statCard}>
              <div className={`${styles.statIcon} ${styles.pending}`}>‚è≥</div>
              <div className={styles.statContent}>
                <span className={styles.statLabel}>En attente</span>
                <span className={`${styles.statValue} ${styles.pending}`}>
                  {stats.pending}
                </span>
              </div>
            </div>

            <div className={styles.statCard}>
              <div className={`${styles.statIcon} ${styles.inProgress}`}>üîÑ</div>
              <div className={styles.statContent}>
                <span className={styles.statLabel}>En cours</span>
                <span className={`${styles.statValue} ${styles.inProgress}`}>
                  {stats.inProgress}
                </span>
              </div>
            </div>

            <div className={styles.statCard}>
              <div className={`${styles.statIcon} ${styles.completed}`}>‚úÖ</div>
              <div className={styles.statContent}>
                <span className={styles.statLabel}>Termin√©</span>
                <span className={`${styles.statValue} ${styles.completed}`}>
                  {stats.completed}
                </span>
              </div>
            </div>

            <div className={styles.statCard}>
              <div className={`${styles.statIcon} ${styles.assigned}`}>üë•</div>
              <div className={styles.statContent}>
                <span className={styles.statLabel}>Assign√©es</span>
                <span className={`${styles.statValue} ${styles.assigned}`}>
                  {stats.assigned}
                </span>
              </div>
            </div>
          </div>

          <div className={styles.filtersSection}>
            <div className={styles.filterGroup}>
              <label htmlFor="search" className={styles.filterLabel}>
                Rechercher
              </label>
              <input
                type="text"
                id="search"
                name="search"
                value={filters.search}
                onChange={handleFilterChange}
                placeholder="Rechercher une t√¢che..."
                className={styles.filterInput}
              />
            </div>

            <div className={styles.filterGroup}>
              <label htmlFor="status" className={styles.filterLabel}>
                Statut
              </label>
              <select
                id="status"
                name="status"
                value={filters.status}
                onChange={handleFilterChange}
                className={styles.filterSelect}
              >
                <option value="">Tous les statuts</option>
                <option value="pending">En attente</option>
                <option value="in-progress">En cours</option>
                <option value="completed">Termin√©</option>
              </select>
            </div>
          </div>

          {loading ? (
            <div className={styles.loadingState}>Chargement des t√¢ches...</div>
          ) : !tasks || tasks.length === 0 ? (
            <div className={styles.emptyState}>
              <p className={styles.emptyText}>Aucune t√¢che trouv√©e</p>
              <button onClick={handleCreateTask} className={styles.emptyButton}>
                Cr√©er la premi√®re t√¢che
              </button>
            </div>
          ) : (
            <div className={styles.tasksList}>
              {tasks && tasks.map(task => (
                <TaskItem
                  key={task.id}
                  task={task}
                  onEdit={handleEditTask}
                  onDelete={handleDeleteTask}
                  isAdmin={true}
                />
              ))}
            </div>
          )}
        </div>
      </main>

      <FormModal
        isOpen={isFormModalOpen}
        onClose={() => setIsFormModalOpen(false)}
      >
        <TaskForm
          task={selectedTask}
          users={users}
          isAdmin={true}
          onSubmit={handleSubmitTask}
          onCancel={() => setIsFormModalOpen(false)}
        />
      </FormModal>

      <Modal
        isOpen={modal.isOpen}
        onClose={closeModal}
        title={modal.title}
        message={modal.message}
        type={modal.type}
      />
    </div>
  );
};

export default Dashboard;
