import React from 'react';
import { useNavigate, useLocation, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import styles from './Header.module.css';

const Header: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { user, logout, isAdmin } = useAuth();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const getInitials = (name: string): string => {
    const parts = name.split(' ');
    if (parts.length >= 2) {
      return `${parts[0][0]}${parts[1][0]}`;
    }
    return name.substring(0, 2);
  };

  return (
    <header className={styles.header}>
      <div className={styles.headerContent}>
        {/* Logo AROLITEC */}
        <div className={styles.logo}>AROLITEC</div>

        {/* Navigation (visible uniquement pour admin) */}
        {isAdmin() && (
          <nav className={styles.navigation}>
            <Link
              to="/"
              className={`${styles.navLink} ${location.pathname === '/' ? styles.navLinkActive : ''}`}
            >
              Mes Tâches
            </Link>
            <Link
              to="/dashboard"
              className={`${styles.navLink} ${location.pathname === '/dashboard' ? styles.navLinkActive : ''}`}
            >
              Dashboard
            </Link>
          </nav>
        )}

        {/* Section utilisateur + déconnexion */}
        <div className={styles.userSection}>
          {/* Info utilisateur */}
          <div className={styles.userInfo}>
            <div className={styles.avatar}>
              {user && getInitials(user.name)}
            </div>
            <div className={styles.userDetails}>
              <div className={styles.userName}>
                {user?.name}
                {isAdmin() && <span className={styles.adminBadge}>Admin</span>}
              </div>
              <div className={styles.userRole}>
                {isAdmin() ? 'Administrateur' : 'Utilisateur'}
              </div>
            </div>
          </div>

          {/* Bouton déconnexion minimaliste */}
          <button onClick={handleLogout} className={styles.logoutButton}>
            <span className={styles.logoutIcon}>⎋</span>
            <span>Déconnexion</span>
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
