import React from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate, useLocation } from 'react-router-dom';
import styles from './Header.module.css';

const Header: React.FC = () => {
  const { user, logout, isAdmin } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const goToDashboard = () => {
    navigate('/dashboard');
  };

  const goToMyTasks = () => {
    navigate('/');
  };

  const getInitials = (name: string): string => {
    const parts = name.split(' ');
    if (parts.length >= 2) {
      return `${parts[0][0]}${parts[1][0]}`;
    }
    return name.substring(0, 2);
  };

  return (
    <header className={styles.header}>
      <div className={styles.container}>
        <div className={styles.leftSection}>
          <h1 className={styles.logo}>AROLITEC</h1>
          {user && isAdmin() && (
            <nav className={styles.nav}>
              <button 
                onClick={goToMyTasks} 
                className={`${styles.navButton} ${location.pathname === '/' ? styles.navButtonActive : ''}`}
              >
                Mes Tâches
              </button>
              <button 
                onClick={goToDashboard} 
                className={`${styles.navButton} ${location.pathname === '/dashboard' ? styles.navButtonActive : ''}`}
              >
                Dashboard
              </button>
            </nav>
          )}
        </div>
        
        {user && (
          <div className={styles.rightSection}>
            <div className={styles.userInfo}>
              <div className={styles.avatar}>
                {getInitials(user.name)}
              </div>
              <div className={styles.userDetails}>
                <span className={styles.userName}>{user.name}</span>
                {isAdmin() && <span className={styles.adminBadge}>Admin</span>}
              </div>
            </div>
            <button onClick={handleLogout} className={styles.logoutButton}>
              <span className={styles.logoutIcon}>⎋</span>
              <span className={styles.logoutText}>Déconnexion</span>
            </button>
          </div>
        )}
      </div>
    </header>
  );
};

export default Header;
